namespace Zlib
{
    using System;
    using System.Runtime.InteropServices;

    /// <summary>A general purpose exception class for exceptions in the Zlib library.</summary>
    [Serializable]
    [Guid("ebc25cf6-9120-4283-b972-0e5520d0000E")]
    public class ZlibException : Exception
    {
        /// <summary>The ZlibException class captures exception information generated by the Zlib library.</summary>
        public ZlibException()
        {
        }

        /// <summary>This ctor collects a message attached to the exception.</summary><param name="s"></param>
        public ZlibException(string s) : base(s)
        {
        }
    }
}